(function(){"use strict";function hideBanner(){document.getElementById(BANNER.id).style.display="none"}function showBanner(){document.getElementById(BANNER.id).style.display="block"}function dismissedId(id){return"dismissed_"+id}function dismissBanner(bannerId){hideBanner();localStorage.setItem(dismissedId(bannerId),(new Date).toISOString());return}function lastTimeDismissed(bannerId){return localStorage.getItem(dismissedId(bannerId))}function init(){if(BANNER.info.dismissible){let dismissed=lastTimeDismissed(BANNER.id);if(dismissed){if(typeof BANNER.info.show_again_in==="number"){const nowDate=new Date;const dismissedDate=new Date(dismissed);const elapsed=nowDate-dismissedDate;if(elapsed>BANNER.info.show_again_in){showBanner()}else{hideBanner()}}else{hideBanner()}}else{showBanner()}document.querySelector("#"+BANNER.id+" .dismiss_banner").addEventListener("click",(ev=>dismissBanner(BANNER.id)));document.getElementById("purchaseLink").addEventListener("click",(ev=>hideBanner(BANNER.id)))}}document.addEventListener("DOMContentLoaded",init)})();
